package StarPlatinum;

public class jotaro5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*编写程序， 判断一个变量x的值，如果是1，输出x=1，如果是5，
		输出x=5，如果是 10，输出x=10，除了以上几个值，都输出x=none。*/
		int x = 11;
		if(x ==1){
			System.out.println("x的值为"+1);
		}
		else if(x == 5){
			System.out.println("x的值为"+5);
		}
		else if(x == 10){
			System.out.println("x的值为"+10);
		}
		else{
			System.out.println("x的值为"+null);
		}
		
	}

}
